import * as PIXI from "pixi.js";

import { Deck } from "./Deck";
import { Card } from "./Card";

export class NewPlayer {
    constructor(app, sheet, state, isPlayer) {
        this.app = app;
        this.sheet = sheet;
        this.state = state;

        this.positions = {
            hand: {x: 240, y: 635},
            field: {x: 240, y: 475},
            shield: {x: 840, y: 475},
            tavern: {x: 90, y: 635},
            grave: {x: 90, y: 475},
            castle: {x: 1110, y: 635},
            jester: {x: 1110, y: 475}
        };

        this.cardsContainer = new PIXI.Container();
        this.cardsContainer.sortableChildren = true;
        this.app.stage.addChild(this.cardsContainer);

        this.handCount = state.handCount;

        this.tavern = new Deck(app, sheet, "tile028.jpg", this.positions.tavern, state.tavern);
        this.grave = new Deck(app, sheet, "tile028.jpg", this.positions.grave, state.grave);
        this.castle = new Deck(app, sheet, "tile013.jpg", this.positions.castle, state.castle);
        this.jester = new Deck(app, sheet, "tile014.jpg", this.positions.jester, state.jester);

        this.hand = [];
        if (isPlayer) {
            for (const index in state.hand) {
                const card = new Card(this.cardsContainer, sheet, state.hand[index], this.positions.hand);
                this.hand.push(card);
            }
        } else {
            for (let step = 0; step < state.handCount; step++) {
                const card = new Card(this.cardsContainer, sheet, "tile028.jpg", this.positions.hand);
                this.hand.push(card);
            }
        }
        this.cleanHand();

        this.field = [];
        for (const index in state.field) {
            const card = new Card(this.cardsContainer, sheet, state.field[index], this.positions.field);
            this.field.push(card);
        }
        this.cleanField();

        this.shield = [];
    }

    cleanHand() {
        for (const index in this.hand) {
            const position = {
                x: this.positions.hand.x + (index * 100),
                y: this.positions.hand.y
            };
            this.hand[index].moveTo(position, true);
        }
    }

    cleanField() {
        for (const index in this.field) {
            const position = {
                x: this.positions.field.x + (index * 100),
                y: this.positions.field.y
            };
            this.field[index].moveTo(position, true);
        }
    }
}
