import * as PIXI from "pixi.js";

import cardsSheet from './assets/cards.json';
import cardsImage from './assets/cards.jpg';

import { Mattress } from "./components/Mattress";
import { Card } from "./components/Card";

import 'globalthis/polyfill';
import { PlayerHand } from "./components/PlayerHand";

export class Game {
    constructor({ ref }) {
        this.ref = ref;
        this.app = new PIXI.Application({
            resizeTo: window,
            backgroundColor: 0x5BBA6F,
        });
        global.__PIXI_APP__ = this.app;
        this.sheet = new PIXI.Spritesheet(
            PIXI.BaseTexture.from(cardsImage),
            cardsSheet
        );
        this.mattress = new Mattress({ app:this.app });

        // Cards Container
        this.cardsContainer = new PIXI.Container();
        this.cardsContainer.sortableChildren = true;
        this.app.stage.addChild(this.cardsContainer);
    }

    loadAssets() {
        this.sheet.parse(); // see docs, needs await for some reason!!!
    }

    start() {
        this.ref.current.appendChild(this.app.view);
        this.loadAssets();
        this.app.start();

        // Card
        this.hand = new PlayerHand(this.cardsContainer, this.sheet, ["tile036.jpg", "tile037.jpg", "tile038.jpg"]);
        // this.card = new Card(this.cardsContainer, this.sheet, "tile038.jpg", {x:400, y:400});
    }

    end() {
        this.sheet.destroy(true);
        this.app.destroy(true, true);
    }
}
