import * as PIXI from "pixi.js";
import { Card } from "./Card";

export class Shield {
    constructor(cardsContainer, sheet, position, units) {
        this.cards = [];
        this.position = position;
        for (const index in units) {
            const card = new Card(cardsContainer, sheet, units[index], this.position);
            this.cards.push(card);
        }
        this.clean();
    }

    clean() {
        for (const index in this.cards) {
            const position = {
                x: this.position.x - (index * 100),
                y: this.position.y
            };
            this.cards[index].sprite.zIndex = index;
            this.cards[index].moveTo(position, true);
        }
    }
}
