import * as PIXI from "pixi.js";
import { Card } from "./Card";

export class PlayerHand {
    constructor(cardsContainer, sheet, hand) {
        this.cards = [];
        this.position = {
            x: 240,
            y: 635
        };
        if (typeof hand == "object") {
            for (const index in hand) {
                const card = new Card(cardsContainer, sheet, hand[index], this.position);
                this.cards.push(card);
            }
        }
        if (typeof hand == "number") {
            for (let step = 0; step < hand; step++) {
                const card = new Card(cardsContainer, sheet, "tile028.jpg", this.position);
                this.cards.push(card);
            }
        }
        this.clean();
    }

    clean() {
        for (const index in this.cards) {
            const gap = this.cards.length > 7 ? 700/this.cards.length : 100;
            const position = {
                x: this.position.x + (index * gap),
                y: this.position.y
            };
            this.cards[index].sprite.zIndex = index;
            this.cards[index].moveTo(position, true);
        }
    }
}
