import * as PIXI from "pixi.js";

export class Player {
    constructor(app, sheet) {
        this.app = app;
        this.sheet = sheet;

        // Cards Container
        this.cardsContainer = new PIXI.Container();
        this.cardsContainer.sortableChildren = true;
        this.app.stage.addChild(this.cardsContainer);

        // Hand
        this.hand = new PlayerHand(this.cardsContainer, this.sheet, ["tile036.jpg", "tile037.jpg", "tile038.jpg"]);

    }
}
