import * as PIXI from "pixi.js";

import { PlayerHand } from "./PlayerHand";
import { Deck } from "./Deck";

export class Player {
    constructor(app, sheet, state) {
        this.app = app;
        this.sheet = sheet;
        this.state = state;

        // Cards Container
        this.cardsContainer = new PIXI.Container();
        this.cardsContainer.sortableChildren = true;
        this.app.stage.addChild(this.cardsContainer);

        // Hand
        this.hand = new PlayerHand(this.cardsContainer, this.sheet, this.state.hand);

        // Tavern
        this.tavern = new Deck(this.app, this.sheet, "tile028.jpg", {x: 90, y: 635}, 22);

        // Grave
        this.grave = new Deck(this.app, this.sheet, "tile028.jpg", {x: 90, y: 475}, 6);

        // Castle
        this.castle = new Deck(this.app, this.sheet, "tile013.jpg", {x: 1135, y: 635}, 3);
    }
}
