import * as PIXI from "pixi.js";

import cardsSheet from './assets/cards.json';
import cardsImage from './assets/cards.jpg';
import { Mattress } from "./components/Mattress";

export class Game {
    constructor({ ref }) {
        this.ref = ref;
        this.app = new PIXI.Application({
            resizeTo: window,
            backgroundColor: 0x5BBA6F,
        });
        this.sheet = new PIXI.Spritesheet(
            texture = PIXI.BaseTexture.from(cardsImage),
            data = cardsSheet
        );
        this.mattress = new Mattress({ app:this.app });
    }

    loadAssets() {
        this.sheet.parse(); // see docs, needs await for some reason!!!
    }

    start() {
        this.ref.current.appendChild(this.app.view);
        this.loadAssets();
        this.app.start();
    }

    end() {
        this.sheet.destroy(true);
        this.app.destroy(true, true);
    }
}
