import { useState } from 'react';

function RoomLobby() {
    const [playerReady, setPlayerReady] = useState(false);

    const handleReady = () => {
        setPlayerReady(!playerReady);
    }

    return (
        <div>
            <h1>Lobby</h1>
            <h3>I am ready: {playerReady.toString()}</h3>
            <input
                type="checkbox"
                checked={playerReady}
                onChange={handleReady}
            />
        </div>
    );
}

export default RoomLobby;
