import * as PIXI from "pixi.js";

export class Game {
    constructor({ ref }) {
        this.app = new PIXI.Application({
            resizeTo: window,
            backgroundColor: 0x5BBA6F,
        });

        ref.current.appendChild(game.app.view);
    }
}
