import * as PIXI from "pixi.js";

import { Hand } from "./Hand";
import { Deck } from "./Deck";
import { Field } from "./Field";

export class Player {
    constructor(app, sheet, state) {
        this.app = app;
        this.sheet = sheet;
        this.state = state;

        // Cards Container
        this.cardsContainer = new PIXI.Container();
        this.cardsContainer.sortableChildren = true;
        this.app.stage.addChild(this.cardsContainer);

        // Hand
        this.hand = new Hand(this.cardsContainer, this.sheet, {x: 240, y: 635}, this.state.hand);
        // Field
        this.field = new Field(this.cardsContainer, this.sheet, {x: 240, y: 535}, this.state.field);
        // Tavern
        this.tavern = new Deck(this.app, this.sheet, "tile028.jpg", {x: 90, y: 635}, this.state.tavern);
        // Grave
        this.grave = new Deck(this.app, this.sheet, "tile028.jpg", {x: 90, y: 475}, this.state.grave);
        // Castle
        this.castle = new Deck(this.app, this.sheet, "tile013.jpg", {x: 1110, y: 635}, this.state.castle);
        // Jester
        this.jester = new Deck(this.app, this.sheet, "tile014.jpg", {x: 1110, y: 475}, this.state.jester);
    }
}
