import * as PIXI from "pixi.js";

export class Board {
    constructor({ app }) {
        // Draw Field Board
        const field = new PIXI.Graphics();
        field.lineStyle(2, 0xFFFFFF, 1);
        field.drawRect(window.innerWidth * 0.2, window.innerHeight * 0.5, 840, 150);
        app.stage.addChild(field);

        // // Draw player board
        // const playerBoardLines = new PIXI.Graphics();
        // playerBoardLines.lineStyle(2, 0xFFFFFF, 1);
        // playerBoardLines.drawRect(30, 560, 120, 150); // Tavern
        // playerBoardLines.drawRect(180, 560, 840, 150); // Hand
        // playerBoardLines.drawRect(1050, 560, 120, 150); // Castle
        // playerBoardLines.drawRect(30, 400, 120, 150); // Grave
        // playerBoardLines.drawRect(180, 400, 840, 150); // Field
        // playerBoardLines.drawRect(1050, 400, 120, 150); // Joker
        // app.stage.addChild(playerBoardLines);

        // Draw enemy board
        const enemyBoardLines = new PIXI.Graphics();
        enemyBoardLines.lineStyle(2, 0xFFFFFF, 1);
        enemyBoardLines.drawRect(30, 20, 120, 150); // Tavern
        enemyBoardLines.drawRect(180, 20, 840, 150); // Hand
        enemyBoardLines.drawRect(1050, 20, 120, 150); // Castle
        enemyBoardLines.drawRect(30, 180, 120, 150); // Grave
        enemyBoardLines.drawRect(180, 180, 840, 150); // Field
        enemyBoardLines.drawRect(1050, 180, 120, 150); // Joker
        app.stage.addChild(enemyBoardLines);
    }
}
