import * as PIXI from "pixi.js";

export class Mattress {
    constructor({ app }) {
        // Draw player board
        const playerBoardLines = new PIXI.Graphics();
        playerBoardLines.lineStyle(2, 0xFFFFFF, 1);
        playerBoardLines.drawRoundedRect(30, 560, 120, 150, 5); // Tavern
        playerBoardLines.drawRoundedRect(180, 560, 840, 150); // Hand
        playerBoardLines.drawRoundedRect(1050, 560, 120, 150); // Castle
        playerBoardLines.drawRoundedRect(30, 400, 120, 150); // Grave
        playerBoardLines.drawRoundedRect(180, 400, 840, 150); // Field
        playerBoardLines.drawRoundedRect(1050, 400, 120, 150); // Joker
        app.stage.addChild(playerBoardLines);

        // Draw enemy board
        const enemyBoardLines = new PIXI.Graphics();
        enemyBoardLines.lineStyle(2, 0xFFFFFF, 1);
        enemyBoardLines.drawRoundedRect(30, 20, 120, 150); // Tavern
        enemyBoardLines.drawRoundedRect(180, 20, 840, 150); // Hand
        enemyBoardLines.drawRoundedRect(1050, 20, 120, 150); // Castle
        enemyBoardLines.drawRoundedRect(30, 180, 120, 150); // Grave
        enemyBoardLines.drawRoundedRect(180, 180, 840, 150); // Field
        enemyBoardLines.drawRoundedRect(1050, 180, 120, 150); // Joker
        app.stage.addChild(enemyBoardLines);
    }
}
