import * as PIXI from "pixi.js";
import { Card } from "./Card";

export class Shield {
    constructor(cardsContainer, sheet, position, units) {
        this.cardsContainer = cardsContainer;
        this.sheet = sheet;
        this.position = position;

        this.cards = [];
        for (const index in units) {
            const card = new Card(this.cardsContainer, this.sheet, units[index], this.position);
            this.cards.push(card);
        }
        this.clean();
    }

    clean() {
        for (const index in this.cards) {
            const position = {
                x: this.position.x - (index * 100),
                y: this.position.y
            };
            this.cards[index].sprite.zIndex = index;
            this.cards[index].moveTo(position, true);
        }
    }

    remove(units) {
        const removedCards = this.cards.filter(card => units.includes(card.name));
        this.cards = this.cards.filter(card => !units.includes(card.name));
        this.clean();
        return(removedCards);
    }

    add(cards) {
        for (const card in cards) {
            this.cards.push(cards[card]);
        }
        this.clean();
    }
}
