import * as PIXI from "pixi.js";

import cardsSheet from './assets/cards.json';
import cardsImage from './assets/cards.jpg';

import { Mattress } from "./components/Mattress";
import { Player } from "./components/Player";
import { Board } from "./components/Board";

export class Game {
    constructor({ ref }) {
        this.ref = ref;
        this.app = new PIXI.Application({
            resizeTo: window,
            backgroundColor: 0x5BBA6F,
        });
        global.__PIXI_APP__ = this.app;
        this.sheet = new PIXI.Spritesheet(
            PIXI.BaseTexture.from(cardsImage),
            cardsSheet
        );
        // this.mattress = new Mattress({ app:this.app });
        this.mattress = new Board({ app:this.app });
    }

    loadAssets() {
        this.sheet.parse(); // see docs, needs await for some reason!!!
    }

    start() {
        this.ref.current.appendChild(this.app.view);
        this.loadAssets();

        const playerState = {
            hand: ["tile036.jpg", "tile037.jpg", "tile038.jpg", "tile039.jpg", "tile001.jpg", "tile002.jpg", "tile003.jpg"],
            handCount: 7,
            field: ["tile026.jpg", "tile027.jpg", "tile040.jpg", "tile041.jpg"],
            shield: ["tile016.jpg", "tile017.jpg"],
            tavern: 25,
            grave: 7,
            castle: 4,
            jester: 1
        };


        const playerPositions = {
            hand: {x: 240, y: 670},
            field: {x: 480, y: 370},
            shield: {x: 900, y: 520},
            tavern: {x: 360, y: 520},
            grave: {x: 260, y: 520},
            castle: {x: 760, y: 520},
            jester: {x: 510, y: 520}
        };

        const opponentPositions = {
            hand: {x: 240, y: 95},
            field: {x: 600, y: 255},
            shield: {x: 240, y: 255},
            tavern: {x: 1110, y: 95},
            grave: {x: 260, y: 255},
            castle: {x: 90, y: 95},
            jester: {x: 90, y: 255}
        };

        this.player = new Player(this.app, this.sheet, playerState, playerPositions, true);
        // this.opponent = new Player(this.app, this.sheet, playerState, opponentPositions, false);

        this.app.start();
    }

    end() {
        this.sheet.destroy(true);
        this.app.destroy(true, true);
    }
}
